(192, 38, 'panels_data', 'a:3:{s:7:"widgets";a:1:{i:0;a:7:{s:4:"type";s:6:"visual";s:5:"title";s:22:"WELCOME TO LIFTEX 2019";s:4:"text";s:375:"LIFTEX 2019 took place from the 15-16 May 2019 at ExCeL London, it is the only dedicated exhibition for the lift, escalator and access industry to be held in the UK.\n\nOrganised by the Lift and Escalator Industry Association (LEIA) and held every three years, it featured 120 UK &amp; international exhibitors showcasing the latest products and services from around the world.";s:6:"filter";s:1:"1";s:11:"panels_info";a:6:{s:5:"class";s:30:"WP_Widget_Black_Studio_TinyMCE";s:4:"grid";i:0;s:4:"cell";i:0;s:2:"id";i:0;s:9:"widget_id";s:36:"be157ad2-ad8e-440a-80ea-7474eb2fd6c1";s:5:"style";a:2:{s:27:"background_image_attachment";b:0;s:18:"background_display";s:4:"tile";}}s:22:"so_sidebar_emulator_id";s:27:"black-studio-tinymce-810000";s:11:"option_name";s:27:"widget_black-studio-tinymce";}}s:5:"grids";a:1:{i:0;a:4:{s:5:"cells";i:1;s:5:"style";a:3:{s:27:"background_image_attachment";b:0;s:18:"background_display";s:4:"tile";s:14:"cell_alignment";s:10:"flex-start";}s:5:"ratio";i:1;s:15:"ratio_direction";s:5:"right";}}s:10:"grid_cells";a:1:{i:0;a:4:{s:4:"grid";i:0;s:5:"index";i:0;s:6:"weight";i:1;s:5:"style";a:0:{}}}}'),
(193, 39, 'panels_data', 'a:3:{s:7:"widgets";a:1:{i:0;a:7:{s:4:"type";s:6:"visual";s:5:"title";s:52:"CHÀO MỪNG ĐẾN VỚI VIETNAM ELEVATOR EXPO 2020";s:4:"text";s:1539:"Triển lãm VIETNAM ELEVATOR EXPO 2020 - Triển lãm thang máy, thang cuốn và phụ kiện quốc tế 2020 sẽ được tổ chức tại Trung tâm triển lãm quốc tế Hà Nội (ICE) - 91 Trần Hưng Đạo Thành phố Hà Nội từ 18 tháng 6 đến 20 tháng 6 năm 2020.\n\nHội chợ triển lãm đã trở thành một sự kiện trong ngành thang máy trên toàn thế giới dưới sự hỗ trợ của các nhà triển lãm trong và ngoài nước, khách tham quan, bạn bè trong ngành này và các đồng nghiệp. Trong triển lãm kéo dài ba ngày, các nhà triển lãm trong và ngoài nước sẽ có nghệ thuật và kỹ năng của riêng họ, trong đó có các sản phẩm và công nghệ tiên tiến.\n\nNgày nay, các tòa tháp cao thống trị quang cảnh trong các thành phố lớn cho thấy con đường phía trước để phát triển các thành phố và thị trấn. Hội chợ thang máy và thang cuốn quốc tế được thiết kế đặc biệt để cung cấp cho bạn một nền tảng cao cấp để chứng kiến ​​sự đổi mới công nghệ, thiết kế thẩm mỹ và sự tiến bộ trong ngành vận tải dọc.\n\nChúng tôi hy vọng rằng bạn sẽ quan tâm đến việc tham gia gian hàng để quảng bá sản phẩm, tìm kiếm đối tác, gặp gỡ và trao đổi kinh nghiệm, đầu tư hợp tác, góp phần thúc đẩy kinh doanh trong tiềm năng của ngành thang máy tại Việt Nam, v.v.";s:6:"filter";s:1:"1";s:22:"so_sidebar_emulator_id";s:28:"black-studio-tinymce-3910000";s:11:"option_name";s:27:"widget_black-studio-tinymce";s:11:"panels_info";a:6:{s:5:"class";s:30:"WP_Widget_Black_Studio_TinyMCE";s:4:"grid";i:0;s:4:"cell";i:0;s:2:"id";i:0;s:9:"widget_id";s:36:"be157ad2-ad8e-440a-80ea-7474eb2fd6c1";s:5:"style";a:2:{s:27:"background_image_attachment";b:0;s:18:"background_display";s:4:"tile";}}}}s:5:"grids";a:1:{i:0;a:4:{s:5:"cells";i:1;s:5:"style";a:4:{s:5:"class";s:15:"introduce_index";s:27:"background_image_attachment";b:0;s:18:"background_display";s:4:"tile";s:14:"cell_alignment";s:10:"flex-start";}s:5:"ratio";i:1;s:15:"