(751, 188, 'panels_data', 'a:3:{s:7:"widgets";a:1:{i:0;a:7:{s:4:"type";s:6:"visual";s:5:"title";s:0:"";s:4:"text";s:650:"Một tính năng cực kỳ phổ biến của sự kiện này luôn là các buổi hội thảo miễn phí. Năm 2019, các diễn giả xuất sắc sẽ đưa ra một loạt các cuộc nói chuyện và thuyết trình chuyên nghiệp về tư duy và tập trung vào các chủ đề như kết nối, sơ tán, hiện đại hóa và an toàn, cũng như mới nhất về các tiêu chuẩn và quy định mới.\n\nCác buổi bắt đầu lúc 11.30 mỗi ngày - vì vậy hãy chắc chắn rằng bạn đến sớm để có chỗ ngồi. Chúng được vận hành trên cơ sở ai đến trước được phục vụ trước.";s:6:"filter";s:1:"1";s:11:"panels_info";a:6:{s:5:"class";s:30:"WP_Widget_Black_Studio_TinyMCE";s:4:"grid";i:0;s:4:"cell";i:0;s:2:"id";i:0;s:9:"widget_id";s:36:"0d5d0834-75b9-43ec-a6b5-91a0eae984bd";s:5:"style";a:2:{s:27:"background_image_attachment";b:0;s:18:"background_display";s:4:"tile";}}s:22:"so_sidebar_emulator_id";s:29:"black-studio-tinymce-18810000";s:11:"option_name";s:27:"widget_black-studio-tinymce";}}s:5:"grids";a:1:{i:0;a:4:{s:5:"cells";i:1;s:5:"style";a:3:{s:27:"background_image_attachment";b:0;s:18:"background_display";s:4:"tile";s:14:"cell_alignment";s:10:"flex-start";}s:5:"ratio";i:1;s:15:"ratio_direction";s:5:"right";}}s:10:"grid_cells";a:1:{i:0;a:4:{s:4:"grid";i:0;s:5:"index";i:0;s:6:"weight";i:1;s:5:"style";a:0:{}}}}'),
