(753, 186, 'panels_data', 'a:3:{s:7:"widgets";a:1:{i:0;a:7:{s:4:"type";s:6:"visual";s:5:"title";s:0:"";s:4:"text";s:858:"Một tính năng cực kỳ hấp của sự kiện này là các buổi hội thảo miễn phí. Vào năm 2020, các diễn giả xuất sắc sẽ đưa ra loạt các cuộc nói chuyện và thuyết trình chuyên nghiệp về tư duy và kết nối các chủ đề như kết nối, sơ tán, hiện đại hóa và an toàn, cũng như mới nhất về các tiêu chuẩn và quy định mới đối với các lĩnh vực liên quan.\n\n<img class="size-medium wp-image-460 aligncenter" src="http://elevatorexpo.com.vn/wp-content/uploads/2019/06/presentation2-600x400.jpg" alt="" width="600" height="400" />\n\nCác buổi  hội thảo bắt đầu lúc 9h30 mỗi ngày - vì vậy hãy chắc chắn rằng bạn đến sớm để có chỗ ngồi. Chúng được vận hành trên cơ sở ai đến trước được phục vụ trước.";s:6:"filter";s:1:"1";s:22:"so_sidebar_emulator_id";s:29:"black-studio-tinymce-18610000";s:11:"option_name";s:27:"widget_black-studio-tinymce";s:11:"panels_info";a:6:{s:5:"class";s:30:"WP_Widget_Black_Studio_TinyMCE";s:4:"grid";i:0;s:4:"cell";i:0;s:2:"id";i:0;s:9:"widget_id";s:36:"0d5d0834-75b9-43ec-a6b5-91a0eae984bd";s:5:"style";a:2:{s:27:"background_image_attachment";b:0;s:18:"background_display";s:4:"tile";}}}}s:5:"grids";a:1:{i:0;a:4:{s:5:"cells";i:1;s:5:"style";a:3:{s:27:"background_image_attachment";b:0;s:18:"background_display";s:4:"tile";s:14:"cell_alignment";s:10:"flex-start";}s:5:"ratio";i:1;s:15:"ratio_direction";s:5:"right";}}s:10:"grid_cells";a:1:{i:0;a:4:{s:4:"grid";i:0;s:5:"index";i:0;s:6:"weight";i:1;s:5:"style";a:0:{}}}}'),
